import styled from "styled-components";

export const Wrapper = styled.div`
  display: flex;
  flex-direction: row;
`;

export const SocialBlock = styled.a`
  display: flex;
  flex-direction: column;
  margin: 0.5rem;
  border-bottom: 1px solid #000000;

  &:hover,
  &:focus,
  &:focus-within {
    color: #ffffff;
    border-bottom: 1px solid #fdd835;
  }
`;
