import styled from "styled-components";

export const Wrapper = styled.div`
  display: flex;
  flex-direction: row;
`;

export const SocialBlock = styled.div`
  display: flex;
  flex-direction: column;
`;


export const StyledIcon = styled.a`
  width: auto;
  font-weight: 600;
  text-decoration: none;
  color: #fdd835;

  &:hover {
    color: #ffffff;
  }
`;
