import React from "react";
import "./styles.scss";
import cn from "classnames";
import StyledButtonLink from "../StyledButtonLink/StyledButtonLink";
// pass in classNames

const HoverableImage = ({ src, alt, link, overlayContent, classNames }) => {
  return (
    <div className={cn("hoverableimage", classNames)}>
      <div className="container">
        <img src={src} alt={alt} className="image" />
        <div className="overlay">
          <div className="text">
            <h3>{overlayContent}</h3>
            <StyledButtonLink link={link}>See it out here</StyledButtonLink>
          </div>
        </div>
      </div>
    </div>
  );
};

export default HoverableImage;
